import React, {Component} from 'react';
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank'
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import SignIn from './components/SignIn/SignIn';
import Register from './components/Register/Register';
import './App.css';
import {Route, Switch} from "react-router-dom";

const initialState = {
    input: '',
    imageUrl: '',
    boxes: [],
    route: 'signin',
    isSignedIn: false,
    errorMessage: '',
    user: {
        id: '',
        name: '',
        email: '',
        entries: 0,
        joined: ''
    }
};

class App extends Component {
    constructor() {
        super();
        this.state = initialState
        this.API_URL = process.env.REACT_APP_API_URL || "http://localhost:3001";
    }

    componentDidMount() {
        const token = window.sessionStorage.getItem('token');
        if (token) {
            fetch(this.API_URL + '/signin', {
                method: 'post',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': token
                }
            })
                .then(resp => resp.json())
                .then(data => {
                    if (data && data.userId) {
                        fetch(this.API_URL + `/profile/${data.userId}`, {
                            method: 'get',
                            headers: {
                                'Content-Type': 'application/json',
                                'Authorization': token
                            }
                        })
                            .then(resp => resp.json())
                            .then(user => {
                                if (user && user.email) {
                                    this.loadUser(user);
                                    this.onRouteChange('home');
                                }
                            })
                    }
                })
                .catch(console.log)
        }
    }

    loadUser = (user) => {
        this.setState({
            user: {
                id: user.id,
                name: user.name,
                email: user.email,
                entries: user.entries,
                joined: user.joined
            }
        })
    };

    calculateFaceLocations = (data) => {
        if (data && data.outputs) {
            const regions = data.outputs[0].data.regions;
            const image = document.getElementById('inputimage');
            const width = Number(image.width);
            const height = Number(image.height);
            const boxes = [];

            for (let i in regions) {
                let box = regions[i].region_info.bounding_box;

                boxes.push({
                    "topRow": box.top_row * height,
                    "leftCol": box.left_col * width,
                    "bottomRow": height - (box.bottom_row * height),
                    "rightCol": width - (box.right_col * width)
                });
            }
            return boxes;
        }
        return []
    };

    setFaceBoxes = (boxes) => {
        if (boxes) {
            this.setState({boxes: boxes});
        }
    };

    onInputChange = (event) => {
        this.setState({input: event.target.value});
    };

    onPictureSubmit = () => {
        this.setState({imageUrl: this.state.input});
        fetch(this.API_URL + '/imageurl', {
            method: 'post',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': window.sessionStorage.getItem('token')
            },
            body: JSON.stringify({
                input: this.state.input
            })
        })
            .then(response => response.json())
            .then(response => {
                if (response.outputs) {
                    fetch(this.API_URL + '/image', {
                        method: 'put',
                        headers: {
                            'Content-Type': 'application/json',
                            'Authorization': window.sessionStorage.getItem('token')
                        },
                        body: JSON.stringify({
                            id: this.state.user.id
                        })
                    })
                        .then(response => response.json())
                        .then(json => {
                            if (json.entries) {
                                this.setState(Object.assign(this.state.user, {entries: json.entries}))
                            } else {
                                this.setState({errorMessage: response.errorMessage});
                            }
                        })
                        .catch(console.log);
                    this.setFaceBoxes(this.calculateFaceLocations(response))
                } else {
                    this.setState({errorMessage: response.errorMessage});
                }
            })
            .catch(err => console.log(err));
    };

    onRouteChange = (route) => {
        if (route === 'signout') {
            this.setState(initialState);
        } else if (route === 'home') {
            this.setState({isSignedIn: true});
        }
        this.setState({route: route});
    };

    render() {
        const {isSignedIn, imageUrl, route, boxes} = this.state;
        return (
            <div className="App">
                <div className='content'>
                    <Navigation isSignedIn={isSignedIn} onRouteChange={this.onRouteChange}/>
                    {route === 'home'
                        ?
                        <div id='app-panel'>
                            <Logo/>
                            <Rank name={this.state.user.name} entries={this.state.user.entries}/>
                            <ImageLinkForm onInputChange={this.onInputChange}
                                           onButtonSubmit={this.onPictureSubmit}/>
                            {this.state.imageUrl && this.state.errorMessage === ''
                            && <FaceRecognition boxes={boxes} imageUrl={imageUrl}/>}
                            {this.state.errorMessage && <h3 id="face-recognition-error-message"
                                                            className="db fw6 lh-copy f6 mw5">{this.state.errorMessage}</h3>}
                        </div>
                        :
                        <Switch>
                            <Route path="/register">
                                <Register apiurl={this.API_URL} loadUser={this.loadUser}
                                          onRouteChange={this.onRouteChange}/>
                            </Route>
                            <Route path={["/signin", "/"]}>
                                <SignIn apiurl={this.API_URL} loadUser={this.loadUser}
                                        onRouteChange={this.onRouteChange}/>
                            </Route>
                        </Switch>
                    }
                </div>
            </div>
        );
    }
}

export default App;
